* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  position: relative;
  display: flex;
  flex-flow: column;
}

#player {
  width: 300px;
  height: 50px;
  border: none;
  border-bottom: 1px solid #1946db;
  outline: none;
  margin: 0 auto;
}

#btn {
  display: block;
  padding: 8px 16px;
  border-radius: 4px;
  color: #1946db;
  text-align: center;
  line-height: 30px;
  margin: 15px auto;
  cursor: pointer;
  text-decoration-style: none;
  background-color: #e1e1e1;
}

#root {
  margin: 45px auto;
}

#root div {
  font-size: 20px;
  color: #666666;
}

.main {
  background-color: #fff;
  width: 300px;
  margin: 0 auto;
  margin-top: 30px;
}

.main>div {
  width: 90px;
  height: 90px;
  border: 1% solid #fff;         
  background-color: mediumpurple;
  border-radius: 3%;
  float: left;
  margin: 1%;
  color: #fff;
  font-size: 50px;
  text-align: center;
  line-height: 90px;
  cursor: pointer;
}

.title {
  width: 100%;
  height: 80px;
  text-align: center;
  line-height: 80px;
  margin-top: 30px;
}

.title>p {
  color: #1946db;
  font-size: 25px;
}

#tip {
  position: absolute;
  z-index: 999;
  width: 450px;
  height: 120px;
  color: #1946db;
  font-size: 30px;
  text-align: center;
  line-height: 120px;
  background-color: rgba(245,155,130, 0.5);
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
}
